import React, { useState } from 'react'

function LoginForm () {
  const [password, setPassword] = useState('')
  const [message, setMessage] = useState('')

  const handleSubmit = async e => {
    e.preventDefault()

    // Your fetch code here, similar to your original code
    try {
      const response = await fetch('http://localhost:4000/user/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ password }) // assuming you have other form fields as well
      })

      const data = await response.json()

      if (data.success) {
        // Login successful, handle success
        setMessage(data.message)
        console.log('JWT Token:', data.token)

        // Redirect to the home page using React Router or other navigation methods
      } else {
        // Login failed, handle error message
        setMessage(data.message)
      }
    } catch (error) {
      // Handle any errors that occurred during the fetch
      console.error('Error:', error)
    }
  }

  const handlePasswordKeyPress = e => {
    if (e.key === 'Enter') {
      handleSubmit(e)
    }
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        {/* Other form fields */}
        <input
          type='password'
          placeholder='Password'
          value={password}
          onChange={e => setPassword(e.target.value)}
          onKeyPress={handlePasswordKeyPress}
        />
        <button type='submit'>Login</button>
      </form>
      <div>{message}</div>
    </div>
  )
}

export default LoginForm
